mapper.select:
select * from
( select * ,row_number() over
( order by mkey, mvalue asc ) n
from (${sqlstr}) a
where 1=1
) a
where a.n > #{start}
and a.n <= #{end}

select * from
( select * ,row_number() over ( order by mtarg,mtargname asc ) n
from pd_authcenter
where 1=1 and companyid = #{bridge.companyid}
and funcode = #{funcode} and morig = #{morig}


        and ( mtarg like '${keyword}%' or mtargname like '${keyword}%')


) a
where a.n > #{start}
and a.n <= #{end}

select count(1) as count from (select * from pd_authcenter
  where 1=1 and companyid = #{bridge.companyid}
  and funcode = #{funcode} and morig = #{morig}


          and ( mtarg like '${keyword}%' or mtargname like '${keyword}%')

  ) a
