mapper.select:
    select max(id) as id
from PD_MIDDATA${bridge.year}
    where 1=1

select

from PD_MIDDATA${year}
where 1=1 and companyid = #{companyid}


        and id = #{midBh}

select

from PD_MIDDATA${bridge.year}
where 1=1 and ${key} = #{value} and companyid = #{bridge.companyid}

select

from PD_MIDDATA${year}
where 1=1 and companyid = #{companyid}


        and ( bh like '${keyword}%' )




order by id,bh

select
pkid,id,companyid,bh,deptorper,month,data,process,name
from
(
select
a.pkid,a.id,b.companyid,b.deptid bh,'1' as deptorper,a.month,isnull(a.data,0) data,isnull(a.process,'') process,b.deptname name
,row_number() over
(
order by
b.deptid,b.deptname asc
) n
from PD_MIDDATA${year} a right join PM_DEPT_MB b on a.bh=b.deptid
and a.companyid = b.companyid and b.yearmonth=#{yearmonth}
where 1=1

    and a.id = #{midBh}

and b.yearmonth=#{yearmonth}
and a.month = #{month}
and b.isleaf=1
and b.companyid = #{companyid}


        and ( b.deptid like '${keyword}%' or b.deptname like '%${keyword}%' )


) a
where a.n > #{start}
and a.n <= #{end}

select
pkid,companyid,id,bh,deptorper,month,data,process,name
from
(
select
a.pkid,c.companyid,a.id,c.userid bh,'2' as deptorper,a.month,isnull(a.data,0) data,isnull(process,'') process,
c.username as name
,row_number() over
(
order by
c.userid,c.username asc
) n
from PD_MIDDATA${year} a
right join PM_USER c on a.bh=c.userid and a.companyid = c.companyid
where 1=1

    and a.id = #{midBh}

and a.month = #{month}
and c.companyid = #{companyid}


        and ( c.userid like '${keyword}%'  or c.username like '%${keyword}%' )


) a
where a.n > #{start}
and a.n <= #{end}
